import{_ as e,o as a,c as l,V as t}from"./chunks/framework.51f721ee.js";const i="/ray-template-doc/assets/dracula.23074b85.png",_=JSON.parse('{"title":"主题","description":"","frontmatter":{},"headers":[],"relativePath":"plugin/vscode/theme/index.md","filePath":"plugin/vscode/theme/index.md","lastUpdated":1702620843000}'),o={name:"plugin/vscode/theme/index.md"},r=t('<h1 id="主题" tabindex="-1">主题 <a class="header-anchor" href="#主题" aria-label="Permalink to &quot;主题&quot;">​</a></h1><h2 id="sublime-material-theme" tabindex="-1">Sublime Material Theme <a class="header-anchor" href="#sublime-material-theme" aria-label="Permalink to &quot;Sublime Material Theme&quot;">​</a></h2><blockquote><p>Sublime Material Theme 是 Sublime Text 编辑器的一款热门配色方案。</p></blockquote><ul><li>基于 Material Design 颜色规范，色彩鲜明、大胆，使用了多种鲜艳的蓝色和粉色等颜色，使得代码在编辑器中更加美观</li><li>支持多种语言的高亮显示，包括 Java、Python、CSS 等常见的编程语言，让代码更加易读、易理解</li><li>提供了多种配套的图标和图形元素，包括文件夹图标、文件类型图标等，使得整个编辑器的界面看起来更加一致、美观</li><li>支持自定义配置，用户可以按照自己的喜好进行颜色、字体等方面的调整，以便更好地适应个人习惯</li></ul><h2 id="evondev-dracula" tabindex="-1">Evondev Dracula <a class="header-anchor" href="#evondev-dracula" aria-label="Permalink to &quot;Evondev Dracula&quot;">​</a></h2><blockquote><p>Evondev Dracula 是一款适用于多种编辑器的主题。</p></blockquote><ul><li>设计简洁、清晰，使用黑色和紫色的深色调，让代码看起来更加醒目、易读</li><li>支持多种编辑器，包括 VS Code、Sublime Text、Atom、JetBrains 等，可以帮助用户在不同的开发环境中保持一致的体验</li><li>配色方案丰富，提供了多种变体，包括经典版、软色版、亮色版等，用户可以根据自己的喜好选择合适的方案</li><li>配套的图标和图形元素丰富，包括文件夹图标、文件类型图标等，可以让整个编辑器的界面看起来更加一致、美观</li></ul><h2 id="github-theme" tabindex="-1">GitHub Theme <a class="header-anchor" href="#github-theme" aria-label="Permalink to &quot;GitHub Theme&quot;">​</a></h2><blockquote><p>GitHub Theme 是一款适用于多种编辑器的主题。</p></blockquote><ul><li>设计简洁、清晰，使用白色和黑色的明暗对比，让代码看起来更加舒适、易读</li><li>支持多种编辑器，包括 VS Code、Sublime Text、Atom、JetBrains 等，可以帮助用户在不同的开发环境中保持一致的体验</li><li>配色方案经典，基于 GitHub 的配色方案，颜色鲜明、对比明显，让代码更加易读、易理解</li><li>配套的图标和图形元素丰富，包括文件夹图标、文件类型图标等，可以让整个编辑器的界面看起来更加一致、美观</li></ul><h2 id="安装步骤" tabindex="-1">安装步骤 <a class="header-anchor" href="#安装步骤" aria-label="Permalink to &quot;安装步骤&quot;">​</a></h2><ul><li><code>vscode</code> 插件中搜索以上的主题</li><li>点击安装，然后按照自己喜好切换主题</li></ul><h2 id="个人推荐" tabindex="-1">个人推荐 <a class="header-anchor" href="#个人推荐" aria-label="Permalink to &quot;个人推荐&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">推荐</p><p>个人比较推荐德古拉（Evondev Dracula New Colour）主题，整体由粉红色、淡蓝色为主色调。</p></div><h2 id="效果" tabindex="-1">效果 <a class="header-anchor" href="#效果" aria-label="Permalink to &quot;效果&quot;">​</a></h2><p><img src="'+i+'" alt=""></p>',16),c=[r];function d(u,n,h,s,m,b){return a(),l("div",null,c)}const v=e(o,[["render",d]]);export{_ as __pageData,v as default};
